INSERT INTO `t_pageinfo` (`page_id`, `lang_id`, `page_titre`, `page_nom`, `page_description`, `page_motCle`) VALUES
(1, 1, 'formations à la gestion de conflit', 'Accueil', 'L''Aikido Verbal est une technique de communication permettant de gérer les conflits qui surviennent sous forme d''attaques verbales. Ennoïa présente ses formations pour vous aider à la gestion des conflits: les disputes, les accusations et les moqueries. Ces cours sont destinés aux entreprises dans le cadre du travail, aux privés et aux couples situés essentiellement en Suisse romande.', 'aikido verbal, communication, agression verbale, gérer, conflit, couple, dispute, employé, attaques, raillerie, gestion de conflit, travail, moquerie, cnv, sans violence, ennoia, suisse, fribourg, romandie, en entreprise'),
(1, 2, 'Home', 'Home', '', ''),
(1, 3, 'Startseite', 'Startseite', '', ''),
(2, 1, 'Menu Principal', 'Menu Principal', 'Répertoire contenant les pages de contenu du site', ''),
(3, 1, 'Menu secondaire', 'Menu secondaire', 'Répertoire contenant les pages informatives (contact, plan d''accès, etc...) du site', ''),
(4, 1, 'Contact', 'Contact', 'Contactez notre organisation sur cette page', 'formulaire, contact'),
(5, 1, 'Notre solution', 'Solution', 'Présentation de l''origine de l''Aikido Verbal et la philosophie d''Ennoïa quand à son application.', 'aikido, verbal, aikido verbal, technique, communication, gérer, agression, verbales'),
(6, 1, 'Qui sommes-nous?', 'Ennoïa', 'Présentation de l''équipe d''Ennoïa, startup Fribourgeoise qui explore de nouvelles façons de travailler ensemble à travers la gouvernance, la communication, la créativité et la confiance en soi.', 'ennoia, startup, gouvernance, holacracy, structures libératrices, aikido verbal, co-création, création, gamification'),
(7, 1, 'Nos formations', 'Formations', 'Description du contenu des formation d''Aikido Verbal proposée par Ennoïa. Formations d''introduction, pratique et avancées', 'aikido verbal, ennoia, formation, communication, agression verbale, dispute'),
(8, 1, 'Promotion', 'Promotion', 'Emmenez un ami avec vous à une de nos formations !\r\n\r\nAmenez un ou des amis et recevez chacun un rabais\r\n\r\nRefaites cette formation pour 50% du prix\r\nAmenez un ami et refaite la formation pour 30% du prix\r\nAmenez deux amis ou plus et refaites cette formation gratuitement\r\n\r\n\r\nNous ne refusons personne pour des raisons financières. Si vous souhaitez participer à ces ateliers mais que vous ne le pouvez pas pour des raisons financières, \r\nparlez-nous de votre situation et nous trouverons ensemble une solution afin de vous permettre d''y participer.', 'aikido verbal, communication, agression verbale, gérer, conflit, couple, dispute, employé, attaques, raillerie, gestion de conflit, travail, moquerie, cnv, sans violence, ennoia, suisse, fribourg, romandie, en entreprise, formation, formation continue, ');
